import React, { useEffect, useState } from "react";
 import { useDispatch, useSelector } from "react-redux";
import InfiniteCarousel from 'react-leaf-carousel';
 import {getAllPartenaires} from '../redux/NosPartenaires/actionPartenaires'
import '../App.css'

const PartenaireSection = () => {
  //get All partenaires
  const partenaires = useSelector((state) => state.partenaireStore.partenaires);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getAllPartenaires());
  }, []);
    return (
        <div className="partenaire-section">
            <br/>
            <h1 className="partenaires-title text-center "> 
                Partenaires
            </h1>
<br/><br/> 
             <InfiniteCarousel   id="carousel"
    breakpoints={[
      {
        breakpoint: 500,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
        },
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
        },
      },
    ]}
    dots={true}
    showSides={false}
    sidesOpacity={.5}
    sideSize={.9}
    slidesToScroll={4}
    slidesToShow={4}
    scrollOnDevice={true}
  >
     {partenaires.map((el)=>(
        <div>

        <img
          alt=''
  src={el.image}  id="carousel-image1"    />
      </div>
     ))}



  </InfiniteCarousel>
  <br/><br/><br/><br/>
        </div>
    )
}

export default PartenaireSection
